import React, { Component } from "react";


import { Card, Button, Form } from 'react-bootstrap';

import './App.css';


export default class App extends Component {
  state = {
    email: "",
    password: ""
  };

  onSubmit = () => {
    alert(this.state.email);
  };

  render() {
    return (
      <div className="warp-content">
        <Card>
          <Form >
            <Card.Body style={{ backgroundColor: 'rgb(44 143 218)' }}>
              <Card.Title>Login</Card.Title>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control type="email" placeholder="Email" value={this.state.email}  required />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control type="password" placeholder="Password" value={this.state.password} onChange={e => this.setState({ password: e.target.value })} required />
              </Form.Group>
            </Card.Body>
            <Card.Footer style={{ backgroundColor: 'rgb(99 175 232)' }}>
              <div className="d-flex justify-content-between">
                <Button variant="danger" type="Button" onClick={this.onSubmit}> Submit</Button>
                <Button variant="link" style={{ color: 'rgb(109 115 119)' }}>Register</Button>
              </div>
            </Card.Footer>
          </Form>
        </Card>
      </div>
    );
  }
}
